def program_page_replacement_algorithms_set_2_fifo(pages, n, capacity):
    s = set()
    indexes = []
    page_faults = 0
    for i in range(0, n):
        if (len(s) < capacity):
            if (pages[i] not in s):
                s.add(pages[i])
                page_faults += 1
                indexes.append(pages[i])
        else:
            if (pages[i] not in s):
                val = indexes.pop(0)
                s.remove(val)
                s.add(pages[i])
                indexes.append(pages[i])
                page_faults += 1
    return page_faults

