    dp = [False] * m
    dp[0] = True
    for i in range(0, n):
        if dp[0] == True:
            return True
        temp = [False] * m
        for j in range(0, m):
            if dp[j] == True:
                if dp[(j + arr[i]) % m] == False:
                    temp[(j + arr[i]) % m] = True
        for j in range(0, m):
            if temp[j] == True:
                dp[j] = True
        dp[arr[i] % m] = True
    return dp[0]

